<script>

export default {
  name: 'PersonalInfo',
  methods: {
    flushVisitor: function () {
      this.$http.get('https://open.fredliang.cn/blog/visitor')
        .then(response => {
          this.visitors = response.data.data
          var count = {
            num: 0
          }
          this.$anime({
            targets: count,
            num: this.visitors,
            round: 1,
            easing: 'linear',
            update: function () {
              var el = document.querySelector('#count')
              el.innerHTML = JSON.stringify(count.num)
            }
          })
        })
        .catch(error => {
          console.log(error.response)
        })
    }
  },
  data () {
    return {
      iconSize: '36px',
      visitors: 0
    }
  },
  mounted: function () {
    this.flushVisitor()
  }
}
</script>

